import React from 'react';
import YouTube from 'react-youtube';

const VideoComponent = () => {
  const opts = {
    height: '390',
    width: '640',
    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 1, // Pour activer la lecture automatique
      playsinline: 1, // Pour la lecture inline sur iOS
      controls: 0, // Pour cacher les contrôles de la vidéo
      mute: 1, // La vidéo doit être muette pour permettre la lecture automatique sur mobile
    },
  };

  return (
    <div className="video-container">
      <YouTube videoId="3cWsSNX9Pag" opts={opts} />
    </div>
  );
};

export default VideoComponent;
