import React, {useEffect, useState} from 'react';
import './App.css';
import VideoComponent from './Video';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // requires a loader
import FAQ from './FAQ';




function App() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState('accueil');
  const [currentSlide, setCurrentSlide] = useState(0);
  const totalSlides = 3;

  useEffect(() => {
    AOS.init({ duration: 500 });
  }, []);

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

  const goToPrevSlide = () => setCurrentSlide(currentSlide - 1 >= 0 ? currentSlide - 1 : totalSlides - 1);
  const goToNextSlide = () => setCurrentSlide(currentSlide + 1 < totalSlides ? currentSlide + 1 : 0);

  const navigateTo = (page) => {
    setCurrentPage(page);
    setIsMenuOpen(false);
  };

  return (
    <div className="App">
      <div className="site-header">
        <div className="header">
          <img src="title.svg" alt="Title" className="header-title-image" />
          <div className="menu-icon-container" onClick={toggleMenu}>
            <img src="menu.svg" alt="Menu" className="menu-icon" />
          </div>
        </div>
        <nav className={`nav-menu ${isMenuOpen ? 'open' : ''}`}>
          <ul>
            <li onClick={() => navigateTo('accueil')}>Accueil</li>
            <li onClick={() => navigateTo('mon-histoire')}>Mon Histoire</li>
            <li onClick={() => navigateTo('faq')}>FAQ</li>
          </ul>
        </nav>
      </div>

      {currentPage === 'faq' ? (
        <FAQ />
      ) : (
        <>
          <div className="space-200px"></div>
          <div data-aos="fade-up" data-aos-duration="2000">
            <VideoComponent />
          </div>
        </>
      )}
    </div>
  );
}

export default App;
